[toolu_01]      1→import { json } from '@sveltejs/kit';
     2→import { exec } from 'child_process';
     3→import { promisify } from 'util';
     4→import type { RequestHandler } from './$types';
     5→
     6→const execAsync = promisify(exec);
     7→const VALID_STATUSES = ['open', 'in_progress', 'blocked', 'closed'];
     8→
     9→export const PATCH: RequestHandler = async ({ params, request }) => {
    10→	const { status, title, description, priority } = await request.json();
    11→
    12→	if (status && !VALID_STATUSES.includes(status)) {
    13→		return json({ error: 'Invalid status' }, { status: 400 });
    14→	}
    15→
    16→	let cmd = `bd update ${params.id}`;
    17→	if (status) cmd += ` --status ${status}`;
    18→	if (title) cmd += ` --title "${title.replace(/"/g, '\\"')}"`;
    19→	if (description !== undefined) cmd += ` --description "${description.replace(/"/g, '\\"')}"`;
    20→	if (priority) cmd += ` --priority ${priority}`;
    21→
    22→	const { stdout, stderr } = await execAsync(cmd);
    23→
    24→	if (stderr) {
    25→		return json({ error: stderr }, { status: 500 });
    26→	}
    27→
    28→	return json({ success: true, message: stdout.trim() });
    29→};
    30→
    31→export const DELETE: RequestHandler = async ({ params }) => {
    32→	const { stdout, stderr } = await execAsync(`bd close ${params.id} --reason "Deleted from Kanban"`);
    33→
    34→	if (stderr) {
    35→		return json({ error: stderr }, { status: 500 });
    36→	}
    37→
    38→	return json({ success: true });
    39→};
