     2→import { exec } from 'child_process';
     3→import { promisify } from 'util';
     4→import type { RequestHandler } from './$types';
     5→
     6→const execAsync = promisify(exec);
     7→
     8→export const GET: RequestHandler = async () => {
     9→	const { stdout, stderr } = await execAsync('bd list --json');
    10→
    11→	if (stderr) {
    12→		return json({ error: stderr }, { status: 500 });
    13→	}
    14→
    15→	const issues = JSON.parse(stdout);
    16→	return json({ issues });
    17→};
    18→
    19→export const POST: RequestHandler = async ({ request }) => {
    20→	const { title, description, priority, issue_type } = await request.json();
    21→
    22→	if (!title) {
    23→		return json({ error: 'Title required' }, { status: 400 });
    24→	}
    25→
    26→	let cmd = `bd create "${title.replace(/"/g, '\\"')}"`;
    27→	if (description) cmd += ` --description "${description.replace(/"/g, '\\"')}"`;
    28→	if (priority) cmd += ` --priority ${priority}`;
    29→	if (issue_type) cmd += ` --type ${issue_type}`;
    30→
    31→	const { stdout, stderr } = await execAsync(cmd);
    32→
    33→	if (stderr) {
    34→		return json({ error: stderr }, { status: 500 });
    35→	}
    36→
    37→	return json({ success: true, message: stdout.trim() });
    38→};
